function dragBlock(e){e.dataTransfer.setData("text",e.target.className)}function DropBlock(e){e.preventDefault();let t=document.querySelector(".wsa");t.setAttribute("wsa-position",e.target.getAttribute("landing-pos"));let r=document.querySelectorAll(".wsa-drop-bubble");for(let l=0;l<r.length;l++)r[l].remove();t.classList.remove("dragging")}function AllowDropBlock(e){e.preventDefault()}function endDragBlock(e){let t=document.querySelectorAll(".wsa-drop-bubble");for(let r=0;r<t.length;r++)t[r].remove();e.target.classList.remove("dragging")}